code_comparison.code:

class Foo {
    public function __construct() {
        // Do something.
    }

    public function __destruct() {
        // Do something.
        return;
    }
}


class Foo {
    public function __construct() {
        // Do something.
        return <em>$this</em>;
    }

    public function __destruct() {
        // Do something.
        return <em>false</em>;
    }
}
